import { useEffect, useState } from "react";
import { getEvents } from "../../requests/eventRequest";
import { getAttendanceForEvents } from "../../requests/attendanceRequest";

type ApiState = {
  isLoading: boolean;
  hasError: boolean;
  events: Array<any>;
  attendance: Array<any>;
};

const getEventsAndAttendanceForEvents = async (
  apiState: any,
  setApiState: any
) => {
  setApiState({
    ...apiState,
    isLoading: true
  });
  const events = await getEvents();
  console.log("events", events);
  // @ts-ignore fix this once event type clear
  const eventIds = events.map(event => event.id);
  const attendance = getAttendanceForEvents(eventIds);
  console.log("attendance", attendance);
  console.log("events", events);
  return { attendance, events };
};

export const useAttendanceAndEvents = () => {
  const [apiState, setApiState] = useState({
    isLoading: false,
    hasError: false,
    events: [],
    attendance: []
  });
  useEffect(() => {
    getEventsAndAttendanceForEvents();
  });

  return { ...apiState };
};
