import { useEffect, useState } from "react";
import { getEvents } from "../../requests/eventRequest";
import { getAttendanceForEvents } from "../../requests/attendanceRequest";
import { useEventsState } from "../../context/eventsContext";

type ApiState = {
  isLoading: boolean;
  hasError: boolean;
  errorMessage: string;
  events: Array<any>;
};

const getEventsAndAttendanceForEvents = async (setApiState: any) => {
  setApiState((prevState: ApiState) => ({
    ...prevState,
    isLoading: true
  }));

  try {
    const events = await getEvents();
    console.log("first events", events);
    // const attendance = await getAttendanceForEvents(eventIds);
    setApiState((prevState: ApiState) => ({
      ...prevState,
      isLoading: false
      // events
      // attendance
    }));
    console.log("post");
    const { setEvents, events: contextEvents } = useEventsState();
    // setEvents(events);
    // console.log("events", events);
  } catch (err) {
    setApiState((prevState: ApiState) => ({
      ...prevState,
      data: {},
      error: err,
      isLoading: false,
      hasError: true
    }));
  }
};

export const useAttendanceAndEvents = () => {
  const [apiState, setApiState] = useState({
    isLoading: false,
    hasError: false,
    errorMessage: ""
    // events: []
  });
  useEffect(() => {
    getEventsAndAttendanceForEvents(setApiState);
  }, []);

  const { events } = useEventsState();

  return { ...apiState, events };
};
