// @flow
import React, { useState } from "react";
import { Button, Label, FormGroup, Input, Form, Col } from "reactstrap";
import csv from "csvtojson";
import { bindRawMeetupData } from "features/singleMeetingAnalysis/SingleMeetingAnalysisUtils";
const labelColumns = 3;
const inputColumns = 9;

const AttendancePredictorContainer = () => {
  const [upcomingMeetupData, setUpcomingMeetupData] = useState("");
  const handleChange = (event: any) => {
    setUpcomingMeetupData(event.target.value);
  };

  const submitJSON = (event: any) => {
    event.preventDefault();
    csv()
      .fromString(upcomingMeetupData)
      .then(result => {
        const bindedData = bindRawMeetupData(result);
        setUpcomingMeetupData(bindedData);
      });
  };
  return (
    <div>
      <Form>
        <div>
          <FormGroup row>
            <Label sm={labelColumns}>Event Attendance Data: </Label>
            <Col sm={inputColumns}>
              <Input
                rows={10}
                type="textarea"
                value={upcomingMeetupData}
                placeholder={"Enter csv data here..."}
                onChange={handleChange}
              />
            </Col>
          </FormGroup>
          <div />
        </div>
        <Button color="success" type="submit" onClick={submitJSON}>
          Summarize Data
        </Button>
      </Form>
    </div>
  );
};

export default AttendancePredictorContainer;
