import moment from "moment";

type RawUserData = {
  Name: string;
  Attendance: string;
  "User ID": string;
  Title: string;
  "Event Host": string;
  RSVP: string;
  Guests: string;
  "RSVPed on": string;
  "Joined Group on": string;
  "URL of Member Profile": string;
  Notes: string;
};

type BindedAttendeeData = {
  userId: string | null;
  didAttend: boolean;
  didRSVP: boolean;
  title: string;
  eventHost: string;
  rsvpDate: Date | null;
  dateJoinedGroup: Date | null;
};

const defaultAttendedMarker = "Y";

const attendedMeetup = (user: RawUserData) => {
  return user.Attendance === defaultAttendedMarker ? true : false;
};

const didRSVP = (user: RawUserData) => {
  return user.RSVP === "Yes" ? true : false;
};

export const bindRawMeetupData = (
  meetupData: RawUserData[]
): BindedAttendeeData[] => {
  return meetupData.map((user: RawUserData) => {
    return {
      userId:
        user["User ID"] !== "" ? user["User ID"].replace("user ", "") : null,
      didAttend: attendedMeetup(user),
      didRSVP: didRSVP(user),
      title: user.Title,
      eventHost: user["Event Host"],
      rsvpDate: user["RSVPed on"] !== "" ? new Date(user["RSVPed on"]) : null,
      dateJoinedGroup: new Date(user["Joined Group on"])
    };
  });
};

const eventDate = new Date("03/27/2019");

type RelativeMeetupRegistrationDates = {
  onDayOfEventSignup: number;
  pastThirtyDays: number;
  pastSixMonths: number;
  pastYear: number;
  pastTwoYears: number;
  overTwoYearsAgo: number;
};

const getRelativeRegistrationDate = (
  acc: any,
  attendee: BindedAttendeeData
) => {
  const { didAttend, dateJoinedGroup, didRSVP } = attendee;
  if (!dateJoinedGroup || !didRSVP || !didAttend) {
    return acc.meetupGroupRegistration;
  }
  if (moment(dateJoinedGroup).isSame(eventDate, "day")) {
    return {
      ...acc.meetupGroupRegistration,
      onDayOfEventSignup: acc.meetupGroupRegistration.onDayOfEventSignup += 1
    };
  }

  //   console.log("eventDate", moment(eventDate));
  //   console.log("eventDate -", moment(eventDate).subtract(30, "days"));
  if (
    moment(dateJoinedGroup).isBetween(
      moment(eventDate).subtract(30, "days"),
      moment(eventDate)
    )
  ) {
    return {
      ...acc.meetupGroupRegistration,
      pastThirtyDays: acc.meetupGroupRegistration.pastThirtyDays += 1
    };
  }

  if (
    moment(dateJoinedGroup).isBetween(moment(eventDate).subtract(180, "days"))
  ) {
    return {
      ...acc.meetupGroupRegistration,
      pastYear: acc.meetupGroupRegistration.pastSixMonths += 1
    };
  }

  if (
    moment(dateJoinedGroup).isBetween(
      moment(eventDate).subtract(365, "days"),
      moment(eventDate)
    )
  ) {
    return {
      ...acc.meetupGroupRegistration,
      pastYear: acc.meetupGroupRegistration.pastYear += 1
    };
  }

  if (
    moment(dateJoinedGroup).isBetween(
      moment(eventDate).subtract(710, "days"),
      moment(eventDate)
    )
  ) {
    return {
      ...acc.meetupGroupRegistration,
      pastTwoYears: acc.meetupGroupRegistration.pastTwoYears += 1
    };
  }

  console.log("attendee", attendee);
  return {
    ...acc.meetupGroupRegistration,
    overTwoYearsAgo: acc.meetupGroupRegistration.overTwoYearsAgo += 1
  };
};

export const getMeetupMembersWhoAttendedSummary = (
  attendees: BindedAttendeeData[]
) => {
  return attendees
    .filter(attendee => attendee.didRSVP)
    .reduce(
      (acc, currentAttendee) => {
        return getRelativeRegistrationDate(acc, currentAttendee);
      },
      {
        onDayOfEventSignup: 0,
        pastThirtyDays: 0,
        pastSixMonths: 0,
        pastYear: 0,
        pastTwoYears: 0,
        overTwoYearsAgo: 0
      }
    );
};

export const getSummaryData = (attendees: BindedAttendeeData[]) => {
  return attendees.reduce(
    (acc, currentAttendee) => {
      const { didRSVP, didAttend } = currentAttendee;
      return {
        ...acc,
        totalCount: acc.totalCount + 1,
        numberRSVPs: acc.numberRSVPs + (didRSVP ? 1 : 0),
        numberAttendees: didAttend
          ? acc.numberAttendees + 1
          : acc.numberAttendees,
        attendeesWhoRSVPd:
          didRSVP && didAttend
            ? acc.attendeesWhoRSVPd + 1
            : acc.attendeesWhoRSVPd
        // meetupGroupRegistration: getmeetupGroupRegistrationData(
        //   acc,
        //   currentAttendee
        // )
      };
    },
    {
      totalCount: 0,
      numberRSVPs: 0,
      numberAttendees: 0,
      attendeesWhoRSVPd: 0
      //   meetupGroupRegistration: {
      //     onDayOfEventSignup: 0,
      //     pastThirtyDays: 0,
      //     pastSixMonths: 0,
      //     pastYear: 0,
      //     pastTwoYears: 0,
      //     overTwoYearsAgo: 0
      //   }
    }
  );
};
