import { useEffect, useState } from "react";
import { getEvents } from "../requests/eventRequest";
import { useEventsState } from "../context/eventsContext";

type ApiState = {
  isLoading: boolean;
  hasError: boolean;
  errorMessage: string;
  events: Array<any>;
};

const getEventsAndAttendanceForEvents = async (
  setApiState: any,
  setEvents: any
) => {
  setApiState((prevState: ApiState) => ({
    ...prevState,
    isLoading: true
  }));

  try {
    const events = await getEvents();
    setApiState((prevState: ApiState) => ({
      ...prevState,
      isLoading: false,
      events
    }));
    setEvents(events);
  } catch (err) {
    setApiState((prevState: ApiState) => ({
      ...prevState,
      data: {},
      error: err,
      isLoading: false,
      hasError: true
    }));
  }
};

export const useAttendanceAndEvents = () => {
  const [apiState, setApiState] = useState({
    isLoading: false,
    hasError: false,
    errorMessage: "",
    events: []
  });

  const { setEvents } = useEventsState();
  useEffect(() => {
    getEventsAndAttendanceForEvents(setApiState, setEvents);
  }, []);

  return { ...apiState };
};
